mapper.select:
select 

from up_integral_account

   merch_id = #{merchId}
    and org_code = #{orgCode}
    and integral_account_no = #{integralAccountNo}
    and account_status = #{accountStatus}

select 

from up_integral_account

  change_date = #{changeDate}
   and integral_account_no = #{integralAccountNo}
   and account_status = #{accountStatus}
  limit #{currIndex} , #{pageSize}
